const { PermaDB } = require(".")
const db = new PermaDB("perma.db",{ memory: true, minimize: true, oreload: false})

db.setAsync(db,new Date().toLocaleString(),(process.memoryUsage().heapUsed / 1024 / 1024).toFixed(3).toString()).then(data => console.log(data))
db.allAsync(db).then(data => console.log(data))
