function createNestedObject(str, value) {
    const parts = str.split('.');
    if (parts.length <= 2) return value;

    let obj = {};
    let current = obj;

    for (let i = 0; i < parts.length; i++) {
        const part = parts[i];
        if (i === parts.length - 1) {
            current[part] = value;
        } else {
            current[part] = {};
            current = current[part];
        }
    }

    return obj;
}

const result = createNestedObject("object.property", "value");
console.log(result);
